import { test, expect } from '@playwright/test'

test.describe('Performance Tests', () => {
  test('should meet Core Web Vitals thresholds', async ({ page }) => {
    // Navigate to homepage
    await page.goto('/')

    // Wait for page to fully load
    await page.waitForLoadState('networkidle')

    // Get Web Vitals metrics
    const metrics = await page.evaluate(() => {
      return new Promise((resolve) => {
        const vitals = {}

        // LCP (Largest Contentful Paint)
        new PerformanceObserver((list) => {
          const entries = list.getEntries()
          const lastEntry = entries[entries.length - 1]
          vitals.lcp = lastEntry.startTime
        }).observe({ entryTypes: ['largest-contentful-paint'] })

        // FID would be measured with real user interaction
        // CLS (Cumulative Layout Shift)
        new PerformanceObserver((list) => {
          let cls = 0
          for (const entry of list.getEntries()) {
            if (!entry.hadRecentInput) {
              cls += entry.value
            }
          }
          vitals.cls = cls
        }).observe({ entryTypes: ['layout-shift'] })

        // Give time to collect metrics
        setTimeout(() => resolve(vitals), 3000)
      })
    })

    console.log('Web Vitals:', metrics)

    // Assert LCP is under 2.5 seconds (good threshold)
    expect(metrics.lcp).toBeLessThan(2500)

    // Assert CLS is under 0.1 (good threshold)
    expect(metrics.cls).toBeLessThan(0.1)
  })

  test('should load images efficiently', async ({ page }) => {
    // Monitor network requests
    const imageRequests = []

    page.on('request', request => {
      if (request.resourceType() === 'image') {
        imageRequests.push({
          url: request.url(),
          size: 0
        })
      }
    })

    page.on('response', response => {
      if (response.request().resourceType() === 'image') {
        const request = imageRequests.find(req => req.url === response.url())
        if (request) {
          request.size = parseInt(response.headers()['content-length'] || '0')
        }
      }
    })

    await page.goto('/')
    await page.waitForLoadState('networkidle')

    // Check that images are reasonably sized
    for (const request of imageRequests) {
      if (request.size > 0) {
        // Images should be under 1MB for web optimization
        expect(request.size).toBeLessThan(1024 * 1024)
        console.log(`Image: ${request.url.split('/').pop()} - ${Math.round(request.size / 1024)}KB`)
      }
    }
  })

  test('should have fast page load times', async ({ page }) => {
    const startTime = Date.now()

    await page.goto('/')
    await page.waitForLoadState('domcontentloaded')

    const domLoadTime = Date.now() - startTime

    await page.waitForLoadState('networkidle')
    const fullLoadTime = Date.now() - startTime

    console.log(`DOM Load Time: ${domLoadTime}ms`)
    console.log(`Full Load Time: ${fullLoadTime}ms`)

    // DOM should load within 1.5 seconds
    expect(domLoadTime).toBeLessThan(1500)

    // Full page should load within 3 seconds
    expect(fullLoadTime).toBeLessThan(3000)
  })
})

import { test, expect } from '@playwright/test'

test.describe('Homepage', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('/')
  })

  test('should load successfully', async ({ page }) => {
    await expect(page).toHaveTitle(/Uluwatu Surf School/)
  })

  test('should display main navigation', async ({ page }) => {
    // Check if header exists
    await expect(page.locator('header')).toBeVisible()

    // Check navigation items
    const navItems = ['Home', 'About', 'Courses', 'Instructors', 'Contact']
    for (const item of navItems) {
      await expect(page.getByRole('link', { name: item })).toBeVisible()
    }
  })

  test('should display hero section', async ({ page }) => {
    await expect(page.getByRole('heading', { name: /Master the Waves/ })).toBeVisible()
    await expect(page.getByText(/Experience world-class surf coaching/)).toBeVisible()

    // Check CTA buttons
    await expect(page.getByRole('button', { name: /Start Your Journey/ })).toBeVisible()
    await expect(page.getByRole('button', { name: /Watch Video/ })).toBeVisible()
  })

  test('should display stats overlay', async ({ page }) => {
    await expect(page.getByText('15+')).toBeVisible()
    await expect(page.getByText('Years Experience')).toBeVisible()
    await expect(page.getByText('2000+')).toBeVisible()
    await expect(page.getByText('Students Taught')).toBeVisible()
  })

  test('should be mobile responsive', async ({ page }) => {
    // Test mobile viewport
    await page.setViewportSize({ width: 375, height: 667 })

    // Hero section should still be visible
    await expect(page.getByRole('heading', { name: /Master the Waves/ })).toBeVisible()

    // Mobile menu button should be visible
    await expect(page.locator('button[aria-label="Toggle menu"]')).toBeVisible()
  })

  test('should have accessible navigation', async ({ page }) => {
    // Check skip link
    await expect(page.getByText('Skip to main content')).toBeInViewport()

    // Check heading hierarchy
    const h1 = page.locator('h1')
    await expect(h1).toHaveCount(1)
    await expect(h1).toContainText('Master the Waves')
  })
})

import { test, expect } from '@playwright/test'
import { injectAxe, checkA11y } from 'axe-playwright'

test.describe('Accessibility Tests', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('/')
    await injectAxe(page)
  })

  test('should not have any accessibility violations', async ({ page }) => {
    await checkA11y(page, null, {
      detailedReport: true,
      detailedReportOptions: { html: true }
    })
  })

  test('should have proper heading hierarchy', async ({ page }) => {
    // Check h1 exists and is unique
    const h1Elements = await page.locator('h1').count()
    expect(h1Elements).toBe(1)

    // Check heading order (h1, then h2, etc.)
    const headings = await page.locator('h1, h2, h3, h4, h5, h6').allTextContents()
    expect(headings.length).toBeGreaterThan(1)

    console.log('Heading hierarchy:', headings)
  })

  test('should have keyboard navigation support', async ({ page }) => {
    // Test Tab navigation
    await page.keyboard.press('Tab')

    // Skip link should be focused first
    const focusedElement = await page.evaluate(() => document.activeElement?.textContent)
    expect(focusedElement).toBe('Skip to main content')

    // Continue tabbing through interactive elements
    const interactiveElements = await page.locator('a, button, input, select, textarea').count()
    expect(interactiveElements).toBeGreaterThan(0)
  })

  test('should have proper alt text for images', async ({ page }) => {
    const images = await page.locator('img').all()

    for (const image of images) {
      const altText = await image.getAttribute('alt')
      const src = await image.getAttribute('src')

      // All images should have alt text (can be empty for decorative images)
      expect(altText).not.toBeNull()

      // Non-decorative images should have meaningful alt text
      if (src && !src.includes('decoration')) {
        expect(altText).toBeTruthy()
        expect(altText.length).toBeGreaterThan(3)
      }
    }
  })

  test('should have proper color contrast', async ({ page }) => {
    // This would typically be handled by axe-core, but we can do basic checks
    const textElements = await page.locator('p, h1, h2, h3, h4, h5, h6, span').all()

    for (const element of textElements.slice(0, 10)) { // Check first 10 elements
      const styles = await element.evaluate(el => {
        const computed = window.getComputedStyle(el)
        return {
          color: computed.color,
          backgroundColor: computed.backgroundColor,
          fontSize: computed.fontSize
        }
      })

      // Ensure elements have color styles set
      expect(styles.color).toBeTruthy()
      expect(styles.fontSize).toBeTruthy()
    }
  })

  test('should have proper form labels', async ({ page }) => {
    const formInputs = await page.locator('input[type="text"], input[type="email"], input[type="tel"], textarea').all()

    for (const input of formInputs) {
      const id = await input.getAttribute('id')
      const ariaLabel = await input.getAttribute('aria-label')
      const ariaLabelledBy = await input.getAttribute('aria-labelledby')

      if (id) {
        const label = await page.locator(`label[for="${id}"]`).count()
        expect(label).toBeGreaterThan(0)
      } else {
        // Should have aria-label or aria-labelledby if no label
        expect(ariaLabel || ariaLabelledBy).toBeTruthy()
      }
    }
  })
})

'use client'

import { useState, useEffect } from 'react'
import Link from 'next/link'
import { Button } from './ui/Button'

export default function Header() {
  const [isScrolled, setIsScrolled] = useState(false)
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false)

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 0)
    }
    window.addEventListener('scroll', handleScroll)
    return () => window.removeEventListener('scroll', handleScroll)
  }, [])

  const navItems = [
    { name: 'Courses', href: '#courses' },
    { name: 'Instructors', href: '#instructors' },
    { name: 'About', href: '#about' },
    { name: 'Gallery', href: '#gallery' },
    { name: 'Contact', href: '#contact' }
  ]

  return (
    <header className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${
      isScrolled ? 'bg-white/95 backdrop-blur-md shadow-lg' : 'bg-transparent'
    }`}>
      <nav className="container mx-auto px-4 h-20 flex items-center justify-between">
        {/* Logo */}
        <Link href="/" className="flex items-center space-x-2">
          <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-cyan-400 rounded-lg flex items-center justify-center">
            <span className="text-white font-bold text-xl">🏄</span>
          </div>
          <span className={`font-bold text-xl ${
            isScrolled ? 'text-slate-900' : 'text-white'
          }`}>
            Uluwatu Surf
          </span>
        </Link>

        {/* Desktop Navigation */}
        <div className="hidden md:flex items-center space-x-8">
          {navItems.map((item) => (
            <Link
              key={item.name}
              href={item.href}
              className={`font-medium transition-colors duration-200 hover:text-orange-500 ${
                isScrolled ? 'text-slate-700' : 'text-white'
              }`}
            >
              {item.name}
            </Link>
          ))}
        </div>

        {/* CTA Button */}
        <div className="hidden md:block">
          <Button size="md">
            Book Now
          </Button>
        </div>

        {/* Mobile Menu Button */}
        <button
          onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          className={`md:hidden p-2 ${
            isScrolled ? 'text-slate-900' : 'text-white'
          }`}
        >
          <div className="w-6 h-6 flex flex-col justify-center items-center">
            <span className={`block h-0.5 w-6 bg-current transform transition duration-300 ${
              isMobileMenuOpen ? 'rotate-45 translate-y-1.5' : ''
            }`} />
            <span className={`block h-0.5 w-6 bg-current mt-1 transition duration-300 ${
              isMobileMenuOpen ? 'opacity-0' : ''
            }`} />
            <span className={`block h-0.5 w-6 bg-current mt-1 transform transition duration-300 ${
              isMobileMenuOpen ? '-rotate-45 -translate-y-1.5' : ''
            }`} />
          </div>
        </button>

        {/* Mobile Menu */}
        {isMobileMenuOpen && (
          <div className="absolute top-20 left-0 right-0 bg-white/95 backdrop-blur-md shadow-lg md:hidden">
            <div className="px-4 py-6 space-y-4">
              {navItems.map((item) => (
                <Link
                  key={item.name}
                  href={item.href}
                  className="block text-slate-700 font-medium py-2 hover:text-orange-500 transition-colors"
                  onClick={() => setIsMobileMenuOpen(false)}
                >
                  {item.name}
                </Link>
              ))}
              <div className="pt-4">
                <Button className="w-full">
                  Book Now
                </Button>
              </div>
            </div>
          </div>
        )}
      </nav>
    </header>
  )
}

