@tailwind base;
@tailwind components;
@tailwind utilities;

/* Custom scrollbar - monochromatic */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: #f3f4f6;
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(to bottom, #374151, #111827);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(to bottom, #1f2937, #000000);
}

/* Smooth scrolling for anchor links */
html {
  scroll-behavior: smooth;
}

/* Base styles with refined typography */
body {
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
  line-height: 1.5;
  color: #374151;
  font-weight: 400;
  letter-spacing: -0.01em;
}

/* Typography hierarchy utilities */
.text-display-1 {
  @apply text-6xl md:text-7xl font-light leading-tight tracking-tight;
}

.text-display-2 {
  @apply text-5xl md:text-6xl font-light leading-tight tracking-tight;
}

.text-display-3 {
  @apply text-4xl md:text-5xl font-normal leading-tight tracking-tight;
}

.text-heading-1 {
  @apply text-3xl md:text-4xl font-normal leading-tight tracking-tight;
}

.text-heading-2 {
  @apply text-2xl md:text-3xl font-medium leading-tight tracking-tight;
}

.text-heading-3 {
  @apply text-xl md:text-2xl font-medium leading-tight tracking-tight;
}

.text-body-lg {
  @apply text-lg md:text-xl font-normal leading-relaxed tracking-tight;
}

.text-body {
  @apply text-base font-normal leading-relaxed tracking-tight;
}

.text-body-sm {
  @apply text-sm font-normal leading-relaxed tracking-tight;
}

/* Custom focus styles */
*:focus {
  outline: none;
}

/* Enhanced spacing utilities */
.section-spacing {
  @apply py-20 md:py-24;
}

.section-spacing-sm {
  @apply py-16 md:py-20;
}

.section-spacing-lg {
  @apply py-24 md:py-32;
}

.content-spacing {
  @apply space-y-6 md:space-y-8;
}

.content-spacing-sm {
  @apply space-y-4 md:space-y-6;
}

.content-spacing-lg {
  @apply space-y-8 md:space-y-12;
}

/* Enhanced button components */
@layer components {
  .btn-primary {
    @apply bg-black text-white font-medium py-4 px-8 rounded-xl transition-all duration-300 hover:bg-gray-900 hover:shadow-lg hover:shadow-black/10 hover:-translate-y-0.5 focus:ring-2 focus:ring-gray-500 focus:ring-offset-2;
  }

  .btn-secondary {
    @apply bg-white text-black border border-black font-medium py-4 px-8 rounded-xl transition-all duration-300 hover:bg-gray-50 hover:shadow-lg hover:shadow-black/10 hover:-translate-y-0.5 focus:ring-2 focus:ring-gray-500 focus:ring-offset-2;
  }

  .btn-outline {
    @apply border-2 border-black text-black font-medium py-4 px-8 rounded-xl transition-all duration-300 hover:bg-black hover:text-white hover:shadow-lg hover:shadow-black/10 hover:-translate-y-0.5 focus:ring-2 focus:ring-gray-500 focus:ring-offset-2;
  }
}

/* Animation classes */
@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
}

@keyframes wave {
  0%, 100% {
    transform: rotate(0deg);
  }
  25% {
    transform: rotate(5deg);
  }
  75% {
    transform: rotate(-5deg);
  }
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideInRight {
  from {
    opacity: 0;
    transform: translateX(30px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes scaleIn {
  from {
    opacity: 0;
    transform: scale(0.95);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

.animate-float {
  animation: float 3s ease-in-out infinite;
}

.animate-wave {
  animation: wave 2s ease-in-out infinite;
}

.animate-fadeInUp {
  animation: fadeInUp 0.6s ease-out forwards;
}

.animate-slideInRight {
  animation: slideInRight 0.6s ease-out forwards;
}

.animate-scaleIn {
  animation: scaleIn 0.4s ease-out forwards;
}

/* Custom utilities - Monochromatic */
.text-gradient {
  @apply text-transparent bg-clip-text bg-gradient-to-r from-gray-900 to-black;
}

.bg-monochrome {
  background: linear-gradient(135deg, #000000 0%, #374151 50%, #6b7280 100%);
}

.bg-neutral {
  background: linear-gradient(135deg, #f9fafb 0%, #f3f4f6 50%, #e5e7eb 100%);
}

.bg-wave-pattern {
  background-image: url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23e2e8f0' fill-opacity='0.1'%3E%3Cpath d='M36 34v-4h-2v4h-4v2h4v4h2v-4h4v-2h-4zm0-30V0h-2v4h-4v2h4v4h2V6h4V4h-4zM6 34v-4H4v4H0v2h4v4h2v-4h4v-2H6zM6 4V0H4v4H0v2h4v4h2V6h4V4H6z'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E");
}

/* Backdrop blur fallback */
.backdrop-blur-fallback {
  background-color: rgba(255, 255, 255, 0.95);
}

/* Loading states */
.loading {
  @apply animate-pulse;
}

.loading-text {
  background: linear-gradient(90deg, #e2e8f0 25%, #f8fafc 50%, #e2e8f0 75%);
  background-size: 200% 100%;
  animation: loading-wave 1.5s infinite;
}

@keyframes loading-wave {
  0% {
    background-position: -200% 0;
  }
  100% {
    background-position: 200% 0;
  }
}

/* Mobile optimizations */
@media (max-width: 640px) {
  .container {
    @apply px-4;
  }

  h1 {
    @apply text-4xl leading-tight;
  }

  h2 {
    @apply text-3xl leading-tight;
  }
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  .btn-primary {
    @apply border-2 border-black;
  }

  .btn-outline {
    @apply border-2 border-black text-black hover:bg-black hover:text-white;
  }
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
  * {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }

  html {
    scroll-behavior: auto;
  }
}

/* Print styles */
@media print {
  .no-print {
    display: none !important;
  }

  body {
    font-size: 12pt;
    line-height: 1.4;
  }

  h1, h2, h3 {
    page-break-after: avoid;
  }
}

