toolUseResult.originalFile:
@tailwind base;
@tailwind components;
@tailwind utilities;

/* Custom scrollbar - monochromatic */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: #f3f4f6;
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(to bottom, #374151, #111827);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(to bottom, #1f2937, #000000);
}

/* Smooth scrolling for anchor links */
html {
  scroll-behavior: smooth;
}

/* Base styles */
body {
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
  line-height: 1.6;
  color: #334155;
}

/* Custom focus styles */
*:focus {
  outline: none;
}

/* Button hover effects - monochromatic */
@layer components {
  .btn-primary {
    @apply bg-black text-white font-semibold py-3 px-6 rounded-lg transition-all duration-300 hover:bg-gray-900 hover:shadow-lg hover:-translate-y-0.5 focus:ring-2 focus:ring-gray-500 focus:ring-offset-2;
  }

  .btn-secondary {
    @apply bg-white text-black border border-black font-semibold py-3 px-6 rounded-lg transition-all duration-300 hover:bg-gray-100 hover:shadow-lg hover:-translate-y-0.5 focus:ring-2 focus:ring-gray-500 focus:ring-offset-2;
  }

  .btn-outline {
