      <div className="absolute inset-0 bg-gradient-to-br from-black/40 via-black/30 to-black/20" />
      <div className="absolute inset-0 bg-[radial-gradient(circle_at_top,_rgba(255,255,255,0.08)_0,rgba(0,0,0,0)_45%)]" />

