import React from 'react'

interface ButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: 'primary' | 'secondary' | 'outline'
  size?: 'sm' | 'md' | 'lg'
  children: React.ReactNode
}

export const Button: React.FC<ButtonProps> = ({ 
  variant = 'primary', 
  size = 'md', 
  children, 
  className = '',
  ...props 
}) => {
  const baseStyles = 'font-semibold rounded-lg transition-all duration-300 focus:outline-none focus:ring-2 focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed'

  const variants = {
    primary: 'bg-black text-white hover:bg-gray-900 hover:shadow-lg hover:-translate-y-0.5 focus:ring-gray-500',
    secondary: 'bg-white text-black border border-black hover:bg-gray-100 hover:shadow-lg hover:-translate-y-0.5 focus:ring-gray-500',
    outline: 'border-2 border-black text-black hover:bg-black hover:text-white hover:shadow-lg hover:-translate-y-0.5 focus:ring-gray-500'
  }

  const sizes = {
    sm: 'px-4 py-2 text-sm',
    md: 'px-6 py-3 text-base',
    lg: 'px-8 py-4 text-lg'
  }

  return (
    <button
      className={`${baseStyles} ${variants[variant]} ${sizes[size]} ${className}`}
      {...props}
    >
      {children}
    </button>
  )
}

@tailwind base;
@tailwind components;
@tailwind utilities;

/* Custom scrollbar - monochromatic */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: #f3f4f6;
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(to bottom, #374151, #111827);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(to bottom, #1f2937, #000000);
}

/* Smooth scrolling for anchor links */
html {
  scroll-behavior: smooth;
}

/* Base styles */
body {
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
  line-height: 1.6;
  color: #334155;
}

/* Custom focus styles */
*:focus {
  outline: none;
}

/* Button hover effects - monochromatic */
@layer components {
  .btn-primary {
    @apply bg-black text-white font-semibold py-3 px-6 rounded-lg transition-all duration-300 hover:bg-gray-900 hover:shadow-lg hover:-translate-y-0.5 focus:ring-2 focus:ring-gray-500 focus:ring-offset-2;
  }

  .btn-secondary {
    @apply bg-white text-black border border-black font-semibold py-3 px-6 rounded-lg transition-all duration-300 hover:bg-gray-100 hover:shadow-lg hover:-translate-y-0.5 focus:ring-2 focus:ring-gray-500 focus:ring-offset-2;
  }

  .btn-outline {
    @apply border-2 border-black text-black font-semibold py-3 px-6 rounded-lg transition-all duration-300 hover:bg-black hover:text-white hover:shadow-lg hover:-translate-y-0.5 focus:ring-2 focus:ring-gray-500 focus:ring-offset-2;
  }
}

/* Animation classes */
@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
}

@keyframes wave {
  0%, 100% {
    transform: rotate(0deg);
  }
  25% {
    transform: rotate(5deg);
  }
  75% {
    transform: rotate(-5deg);
  }
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideInRight {
  from {
    opacity: 0;
    transform: translateX(30px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes scaleIn {
  from {
    opacity: 0;
    transform: scale(0.95);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

.animate-float {
  animation: float 3s ease-in-out infinite;
}

.animate-wave {
  animation: wave 2s ease-in-out infinite;
}

.animate-fadeInUp {
  animation: fadeInUp 0.6s ease-out forwards;
}

.animate-slideInRight {
  animation: slideInRight 0.6s ease-out forwards;
}

.animate-scaleIn {
  animation: scaleIn 0.4s ease-out forwards;
}

/* Custom utilities - Monochromatic */
.text-gradient {
  @apply text-transparent bg-clip-text bg-gradient-to-r from-gray-900 to-black;
}

.bg-monochrome {
  background: linear-gradient(135deg, #000000 0%, #374151 50%, #6b7280 100%);
}

.bg-neutral {
  background: linear-gradient(135deg, #f9fafb 0%, #f3f4f6 50%, #e5e7eb 100%);
}

.bg-wave-pattern {
  background-image: url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23e2e8f0' fill-opacity='0.1'%3E%3Cpath d='M36 34v-4h-2v4h-4v2h4v4h2v-4h4v-2h-4zm0-30V0h-2v4h-4v2h4v4h2V6h4V4h-4zM6 34v-4H4v4H0v2h4v4h2v-4h4v-2H6zM6 4V0H4v4H0v2h4v4h2V6h4V4H6z'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E");
}

/* Backdrop blur fallback */
.backdrop-blur-fallback {
  background-color: rgba(255, 255, 255, 0.95);
}

/* Loading states */
.loading {
  @apply animate-pulse;
}

.loading-text {
  background: linear-gradient(90deg, #e2e8f0 25%, #f8fafc 50%, #e2e8f0 75%);
  background-size: 200% 100%;
  animation: loading-wave 1.5s infinite;
}

@keyframes loading-wave {
  0% {
    background-position: -200% 0;
  }
  100% {
    background-position: 200% 0;
  }
}

/* Mobile optimizations */
@media (max-width: 640px) {
  .container {
    @apply px-4;
  }

  h1 {
    @apply text-4xl leading-tight;
  }

  h2 {
    @apply text-3xl leading-tight;
  }
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  .btn-primary {
    @apply border-2 border-black;
  }

  .btn-outline {
    @apply border-2 border-black text-black hover:bg-black hover:text-white;
  }
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
  * {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }

  html {
    scroll-behavior: auto;
  }
}

/* Print styles */
@media print {
  .no-print {
    display: none !important;
  }

  body {
    font-size: 12pt;
    line-height: 1.4;
  }

  h1, h2, h3 {
    page-break-after: avoid;
  }
}

'use client'

import { useState, useEffect } from 'react'
import Link from 'next/link'
import Image from 'next/image'
import { Button } from './ui/Button'

export default function Header() {
  const [isScrolled, setIsScrolled] = useState(false)
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false)

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 0)
    }
    window.addEventListener('scroll', handleScroll)
    return () => window.removeEventListener('scroll', handleScroll)
  }, [])

  const navItems = [
    { name: 'Pricing & Packages', href: '#pricing' },
    { name: 'Meet Scoot', href: '#scoot' },
    { name: 'Book Session', href: '#book' },
    { name: 'Find Us', href: '#find-us' }
  ]

  return (
    <header className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${
      isScrolled ? 'bg-white/95 backdrop-blur-md shadow-lg border-b border-gray-200' : 'bg-transparent'
    }`}>
      <nav className="container mx-auto px-4 h-20 flex items-center justify-between">
        {/* Logo */}
        <Link href="/" className="flex items-center space-x-3">
          <div className="w-14 h-14 transition-all duration-300">
            <Image
              src="/images/logo.jpg"
              alt="Uluwatu Surf School - Bali"
              width={56}
              height={56}
              className={`w-full h-full object-contain rounded-full transition-all duration-300 ${
                isScrolled ? 'drop-shadow-md' : 'drop-shadow-lg'
              }`}
            />
          </div>
          <span className={`font-bold text-xl transition-colors duration-300 ${
            isScrolled ? 'text-black' : 'text-white drop-shadow-sm'
          }`}>
            Uluwatu Surf School
          </span>
        </Link>

        {/* Desktop Navigation */}
        <div className="hidden md:flex items-center space-x-8">
          {navItems.map((item) => (
            <Link
              key={item.name}
              href={item.href}
              className={`font-medium transition-colors duration-200 hover:text-gray-600 ${
                isScrolled ? 'text-gray-900' : 'text-white'
              }`}
              onClick={(e) => {
                e.preventDefault()
                const targetId = item.href.substring(1)
                const element = document.getElementById(targetId)
                if (element) {
                  element.scrollIntoView({ behavior: 'smooth' })
                }
              }}
            >
              {item.name}
            </Link>
          ))}
        </div>

        {/* CTA Button */}
        <div className="hidden md:block">
          <Button size="md">
            Book Now
          </Button>
        </div>

        {/* Mobile Menu Button */}
        <button
          onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          className={`md:hidden p-2 ${
            isScrolled ? 'text-slate-900' : 'text-white'
          }`}
        >
          <div className="w-6 h-6 flex flex-col justify-center items-center">
            <span className={`block h-0.5 w-6 bg-current transform transition duration-300 ${
              isMobileMenuOpen ? 'rotate-45 translate-y-1.5' : ''
            }`} />
            <span className={`block h-0.5 w-6 bg-current mt-1 transition duration-300 ${
              isMobileMenuOpen ? 'opacity-0' : ''
            }`} />
            <span className={`block h-0.5 w-6 bg-current mt-1 transform transition duration-300 ${
              isMobileMenuOpen ? '-rotate-45 -translate-y-1.5' : ''
            }`} />
          </div>
        </button>

        {/* Mobile Menu */}
        {isMobileMenuOpen && (
          <div className="absolute top-20 left-0 right-0 bg-white/95 backdrop-blur-md shadow-lg md:hidden">
            <div className="px-4 py-6 space-y-4">
              {navItems.map((item) => (
                <Link
                  key={item.name}
                  href={item.href}
                  className="block text-slate-700 font-medium py-2 hover:text-orange-500 transition-colors"
                  onClick={(e) => {
                    e.preventDefault()
                    setIsMobileMenuOpen(false)
                    const targetId = item.href.substring(1)
                    const element = document.getElementById(targetId)
                    if (element) {
                      element.scrollIntoView({ behavior: 'smooth' })
                    }
                  }}
                >
                  {item.name}
                </Link>
              ))}
              <div className="pt-4">
                <Button className="w-full">
                  Book Now
                </Button>
              </div>
            </div>
          </div>
        )}
      </nav>
    </header>
  )
}

