@tailwind base;
@tailwind components;
@tailwind utilities;

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: #f1f5f9;
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(to bottom, #f97316, #ef4444);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(to bottom, #ea580c, #dc2626);
}

/* Smooth scrolling for anchor links */
html {
  scroll-behavior: smooth;
}

/* Base styles */
body {
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
  line-height: 1.6;
  color: #334155;
}

/* Custom focus styles */
*:focus {
  outline: none;
}

/* Button hover effects */
@layer components {
  .btn-primary {
    @apply bg-gradient-to-r from-orange-500 to-red-500 text-white font-semibold py-3 px-6 rounded-lg transition-all duration-300 hover:from-orange-600 hover:to-red-600 hover:shadow-lg hover:-translate-y-0.5 focus:ring-2 focus:ring-orange-500 focus:ring-offset-2;
  }

  .btn-secondary {
    @apply bg-slate-900 text-white font-semibold py-3 px-6 rounded-lg transition-all duration-300 hover:bg-slate-800 hover:shadow-lg hover:-translate-y-0.5 focus:ring-2 focus:ring-slate-500 focus:ring-offset-2;
  }

  .btn-outline {
    @apply border-2 border-orange-500 text-orange-500 font-semibold py-3 px-6 rounded-lg transition-all duration-300 hover:bg-orange-500 hover:text-white hover:shadow-lg hover:-translate-y-0.5 focus:ring-2 focus:ring-orange-500 focus:ring-offset-2;
  }
}

/* Animation classes */
@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
}

@keyframes wave {
  0%, 100% {
    transform: rotate(0deg);
  }
  25% {
    transform: rotate(5deg);
  }
  75% {
    transform: rotate(-5deg);
  }
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideInRight {
  from {
    opacity: 0;
    transform: translateX(30px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes scaleIn {
  from {
    opacity: 0;
    transform: scale(0.95);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

.animate-float {
  animation: float 3s ease-in-out infinite;
}

.animate-wave {
  animation: wave 2s ease-in-out infinite;
}

.animate-fadeInUp {
  animation: fadeInUp 0.6s ease-out forwards;
}

.animate-slideInRight {
  animation: slideInRight 0.6s ease-out forwards;
}

.animate-scaleIn {
