@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 210 40% 98%;
    --foreground: 222.2 84% 4.9%;
    --card: 0 0% 100%;
    --card-foreground: 222.2 84% 4.9%;
    --popover: 0 0% 100%;
    --popover-foreground: 222.2 84% 4.9%;
    --primary: 221.2 83.2% 53.3%;
    --primary-foreground: 210 40% 98%;
    --secondary: 210 40% 96%;
    --secondary-foreground: 222.2 84% 4.9%;
    --muted: 210 40% 96%;
    --muted-foreground: 215.4 16.3% 46.9%;
    --accent: 210 40% 96%;
    --accent-foreground: 222.2 84% 4.9%;
    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 210 40% 98%;
    --border: 214.3 31.8% 91.4%;
    --input: 214.3 31.8% 91.4%;
    --ring: 221.2 83.2% 53.3%;
    --radius: 0.5rem;
  }

  .dark {
    --background: 222.2 84% 4.9%;
    --foreground: 210 40% 98%;
    --card: 222.2 84% 4.9%;
    --card-foreground: 210 40% 98%;
    --popover: 222.2 84% 4.9%;
    --popover-foreground: 210 40% 98%;
    --primary: 210 40% 98%;
    --primary-foreground: 222.2 84% 4.9%;
    --secondary: 217.2 32.6% 17.5%;
    --secondary-foreground: 210 40% 98%;
    --muted: 217.2 32.6% 17.5%;
    --muted-foreground: 215 20.2% 65.1%;
    --accent: 217.2 32.6% 17.5%;
    --accent-foreground: 210 40% 98%;
    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 210 40% 98%;
    --border: 217.2 32.6% 17.5%;
    --input: 217.2 32.6% 17.5%;
    --ring: 212.7 26.8% 83.9%;
  }

  * {
    @apply border-border;
  }

  html {
    scroll-behavior: smooth;
  }

  body {
    @apply bg-background text-foreground;
    font-family: 'Inter', sans-serif;
    color: theme('colors.charcoal');
    background-color: theme('colors.clean-white');
    line-height: 1.6;
  }
}

@layer components {
  .btn-primary {
    @apply bg-deep-ocean text-white px-6 py-3 font-medium hover:bg-blue-800 transition-colors duration-200;
  }

  .btn-secondary {
    @apply border border-charcoal text-charcoal px-6 py-3 font-medium hover:bg-charcoal hover:text-white transition-colors duration-200;
  }
}

import Script from 'next/script'

export default function StructuredData() {
  const structuredData = {
    "@context": "https://schema.org",
    "@type": "TouristAttraction",
    "name": "Uluwatu Surf School",
    "alternateName": "Professional Surf Instruction Uluwatu",
    "description": "Professional surf instruction in Uluwatu, Bali. Local expertise enhanced by modern analysis technology. Offering group lessons, semi-private, and private surf coaching with video analysis.",
    "url": "https://uluwatusurfschool.com",
    "telephone": "+62-812-3456-7890",
    "email": "info@uluwatusurfschool.com",
    "address": {
      "@type": "PostalAddress",
      "streetAddress": "Uluwatu",
      "addressLocality": "Pecatu",
      "addressRegion": "Bali",
      "postalCode": "80361",
      "addressCountry": "ID"
    },
    "geo": {
      "@type": "GeoCoordinates",
      "latitude": "-8.8299",
      "longitude": "115.0849"
    },
    "openingHours": "Mo-Su 06:00-18:00",
    "priceRange": "IDR 400,000 - IDR 700,000",
    "aggregateRating": {
      "@type": "AggregateRating",
      "ratingValue": "4.9",
      "reviewCount": "150",
      "bestRating": "5",
      "worstRating": "1"
    },
    "founder": {
      "@type": "Person",
      "name": "Scotty",
      "description": "Head Instructor with 15+ years of local surfing experience in Bali"
    },
    "offers": [
      {
        "@type": "Offer",
        "name": "Group Surf Lesson",
        "description": "2-hour group surf lesson with maximum 4 students per instructor",
        "price": "400000",
        "priceCurrency": "IDR"
      },
      {
        "@type": "Offer",
        "name": "Semi-Private Surf Lesson",
        "description": "2-hour semi-private surf lesson with maximum 2 students",
        "price": "500000",
        "priceCurrency": "IDR"
      },
      {
        "@type": "Offer",
        "name": "Private Surf Lesson with Tech Analysis",
        "description": "2.5-hour private surf lesson with video analysis technology",
        "price": "700000",
        "priceCurrency": "IDR"
      }
    ],
    "amenityFeature": [
      {
        "@type": "LocationFeatureSpecification",
        "name": "Equipment Rental",
        "value": "Surfboards and safety equipment included"
      },
      {
        "@type": "LocationFeatureSpecification",
        "name": "Video Analysis",
        "value": "Professional video analysis for technique improvement"
      },
      {
        "@type": "LocationFeatureSpecification",
        "name": "Safety Training",
        "value": "Comprehensive safety briefing and ocean awareness"
      }
    ],
    "sameAs": [
      "https://instagram.com/uluwatusurfschool_official",
      "https://instagram.com/scotty_dex"
    ],
    "knowsAbout": [
      "Uluwatu surf break",
      "Reef break surfing",
      "Surf instruction",
      "Ocean safety",
      "Video analysis",
      "Bali surfing"
    ]
  }

  return (
    <Script
      id="structured-data"
      type="application/ld+json"
      dangerouslySetInnerHTML={{
        __html: JSON.stringify(structuredData)
      }}
    />
  )
}

import type { Config } from 'tailwindcss'

const config: Config = {
  darkMode: ["class"],
  content: [
    './pages/**/*.{js,ts,jsx,tsx,mdx}',
    './components/**/*.{js,ts,jsx,tsx,mdx}',
    './app/**/*.{js,ts,jsx,tsx,mdx}',
  ],
  theme: {
    extend: {
      colors: {
        // Custom surf school colors
        charcoal: '#1E293B',
        'deep-ocean': '#1E3A8A',
        'storm-gray': '#475569',
        'forest-green': '#047857',
        bronze: '#92400E',
        'clean-white': '#F9FAFB',
        // shadcn/ui colors
        border: "hsl(var(--border))",
        input: "hsl(var(--input))",
        ring: "hsl(var(--ring))",
        background: "hsl(var(--background))",
        foreground: "hsl(var(--foreground))",
        primary: {
          DEFAULT: "hsl(var(--primary))",
          foreground: "hsl(var(--primary-foreground))",
        },
        secondary: {
          DEFAULT: "hsl(var(--secondary))",
          foreground: "hsl(var(--secondary-foreground))",
        },
        destructive: {
          DEFAULT: "hsl(var(--destructive))",
          foreground: "hsl(var(--destructive-foreground))",
        },
        muted: {
          DEFAULT: "hsl(var(--muted))",
