import Link from 'next/link'
import { Users, Camera, Target, Map } from 'lucide-react'

const lessons = [
  {
    id: 'lesson-only',
    icon: Users,
    title: 'Lesson Only',
    description: '1.5 hours in water plus 20-30 minutes land coaching for beginners. Professional instruction tailored to your level with location selection based on conditions.',
    features: [
      '1.5 hours in water',
      '20-30 min land coaching (beginners)',
      'All equipment included',
      'Break explanation & safety briefing',
      'Level-appropriate spot selection'
    ],
    prices: {
      private: 'IDR 700k',
      semiPrivate: 'IDR 500k'
    },
    popular: false
  },
  {
    id: 'lesson-video',
    icon: Camera,
    title: 'Lesson + Video Analysis',
    description: 'Enhanced learning with surf video analysis. Professional coaching with video review to accelerate your improvement and technique development.',
    features: [
      '1.5 hours in water',
      '20-30 min land coaching (beginners)',
      'Surf video recording',
      'Video analysis & review',
      'All equipment included',
      'Level-appropriate spot selection'
    ],
    prices: {
      private: 'IDR 800k',
      semiPrivate: 'IDR 600k'
    },
    popular: true
  },
  {
    id: 'guiding',
    icon: Map,
    title: 'Surf Guiding',
    description: 'Guided surf sessions for experienced surfers. Includes spot selection, conditions assessment, and optional video analysis for additional fee.',
    features: [
      'Experienced surfer guidance',
      'Optimal spot selection',
      'Conditions assessment',
      'Break knowledge sharing',
      'Optional video analysis (+IDR 200k)',
      'Board rental not included'
    ],
    price: 'IDR 500k',
    note: 'Per person/session. Board rental not included.',
    popular: false
  }
]

export default function LessonCards() {
  return (
    <section className="py-20 bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-16">
          <h2 className="text-4xl font-light text-charcoal mb-4">
            Lesson Packages
          </h2>
          <p className="text-lg text-storm-gray max-w-2xl mx-auto">
            Professional surf instruction tailored to your learning style and goals. 
            All prices are per person/per session. Equipment and safety briefing included.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {lessons.map((lesson) => {
            const IconComponent = lesson.icon
            return (
              <div 
                key={lesson.id}
                className={`bg-white rounded-lg shadow-sm p-8 relative ${
                  lesson.popular ? 'ring-2 ring-deep-ocean' : ''
                }`}
              >
                {lesson.popular && (
                  <div className="absolute -top-4 left-1/2 transform -translate-x-1/2">
                    <span className="bg-deep-ocean text-white px-4 py-1 text-sm font-medium rounded-full">
                      Most Popular
                    </span>
                  </div>
                )}

                <div className="text-center mb-6">
                  <div className="inline-flex items-center justify-center w-16 h-16 bg-gray-100 rounded-full mb-4">
                    <IconComponent className="h-8 w-8 text-deep-ocean" />
                  </div>
                  <h3 className="text-xl font-semibold text-charcoal mb-2">
                    {lesson.title}
                  </h3>
                  <p className="text-storm-gray text-sm leading-relaxed">
                    {lesson.description}
                  </p>
                </div>

                <div className="space-y-3 mb-8">
                  {lesson.features.map((feature, index) => (
                    <div key={index} className="flex items-center text-sm text-storm-gray">
                      <div className="w-1.5 h-1.5 bg-deep-ocean rounded-full mr-3"></div>
                      {feature}
                    </div>
                  ))}
                </div>

                <div className="text-center border-t pt-6">
                  {lesson.prices ? (
                    <div className="mb-4">
                      <div className="text-lg font-semibold text-charcoal mb-2">
                        Private: <span className="text-2xl">{lesson.prices.private}</span>
                      </div>
                      <div className="text-lg font-semibold text-charcoal">
                        Semi-Private: <span className="text-2xl">{lesson.prices.semiPrivate}</span>
                      </div>
                    </div>
                  ) : (
                    <div className="text-3xl font-semibold text-charcoal mb-2">
                      {lesson.price}
                    </div>
                  )}

                  {lesson.note && (
                    <p className="text-xs text-storm-gray mb-4 italic">
                      {lesson.note}
                    </p>
                  )}

                  <Link
                    href={`/book?lesson=${lesson.id}`}
                    className={`block w-full py-3 text-center font-medium transition-colors duration-200 ${
                      lesson.popular
                        ? 'bg-deep-ocean text-white hover:bg-blue-800'
                        : 'border border-charcoal text-charcoal hover:bg-charcoal hover:text-white'
                    }`}
                  >
                    Book Now
                  </Link>
                </div>
              </div>
            )
          })}
        </div>
      </div>
    </section>
  )
}

